def find_min_threshold_and_days(total_cities, max_day_dist, dists, costs):
    INFTY = 100000001

    def find_num_days(price_threshold):
        curr_pos = 0
        took_days = 0
        while curr_pos < total_cities:
            (can_move, cum_dist) = (0, 0)
            while curr_pos + can_move < total_cities and cum_dist + dists[1 + can_move + curr_pos] <= max_day_dist:
                can_move += 1
                cum_dist += dists[can_move + curr_pos]
            while curr_pos + can_move < total_cities and can_move > 0 and (costs[curr_pos + can_move] > price_threshold):
                can_move -= 1
            if can_move == 0:
                return INFTY
            curr_pos += can_move
            took_days += 1
        return took_days
    L = find_num_days(price_threshold=total_cities + 1)
    (low, high) = (min(costs[1:]) - 1, max(costs[1:]) + 1)
    while high - low > 1:
        mid = (low + high) // 2
        if L < find_num_days(price_threshold=mid):
            low = mid
        else:
            high = mid
    C = high
    return (L, C)

