        return chr(ord(c) + 1)
    s = list(s)
    i = n - 1
    s[i] = next_char(s[i])
    while i >= 0 and i < n:
        if ord(s[i]) >= 97 + p:
            s[i] = 'a'
            i -= 1
            if i >= 0:
                s[i] = next_char(s[i])
        elif i > -1 and s[i] == s[i - 1] or (i > 1 and s[i] == s[i - 2]):
            s[i] = next_char(s[i])
        else:
            i += 1
    if i < 0:
        return 'NO'
    else:
        return ''.join(s)

