def find_min_permutation_score(a):
    from itertools import product
    a = sorted(a)
    if a[0] != a[-1]:
        return 0
    k = 31
    while not (a[0] ^ a[-1]) >> k & 1:
        k -= 1
    return min((x ^ y for (x, y) in product([x for x in a if x >> k & 1], [x for x in a if not x >> k & 1])))

