def count_patties_eaten(N, X):
    a = [2]
    p = [1]
    for i in range(N):
        a.append(a[i] * 2 + 3)
        p.append(p[i] * 2 + 1)

    def dfs(n, x):
        if n == 0:
            return 0 if x <= 0 else 1
        elif x <= 1 + a[n - 1]:
            return dfs(n - 1, x - 1)
        else:
            return p[n - 1] + 1 + dfs(n - 1, x - 2 - a[n - 1])
    return dfs(N, X)

