def max_sequence_sum_after_operations(n, nums):
    total = sum(nums) * -1
    best = 0
    left = 0
    right = 0
    current = 0
    while right < n:
        while current >= 0 and right < n:
            current += nums[right]
            best = max(best, current)
            right += 1
        while current < 0:
            current -= nums[left]
            left += 1
        best = max(best, current)
    return total * (best * 2)

