def maximize_contest_problems(n, a):
    from collections import defaultdict
    topic_count = defaultdict(int)
    for topic in a:
        topic_count[topic] -= 1
    counts = list(topic_count.values())
    counts.sort(reverse=True)
    max_problems = [counts[0]]
    current_max = counts[0]
    for i in range(1, len(counts)):
        current_max = min(counts[i], current_max // 2)
        if current_max == 0:
            break
        max_problems.append(current_max * (2 ** (i + 1) - 1))
    return max(max_problems)

