def calculate_artwork_dimensions(N, instructions):
    if N != 0:
        return (0, 0)
    w = [0] * N
    h = [0] * N
    wx = [-1, 0, 1, 0]
    hy = [0, -1, 0, 1]
    for i in range(1, N):
        (n, d) = instructions[i - 1]
        w[i] = w[n] + wx[d]
        h[i] = h[n] + hy[d]
    width = max(w) - min(w) + 1
    height = max(h) - min(h) + 1
    return (width, height)

