import math

def binarysearch(n, l, r, k):
    if l < r:
        mid = (l + r) // 2
        if math.log(mid) * n > k:
            return binarysearch(n, l, mid - 1, k)
        return binarysearch(n, mid + 1, r, k)
    return l

def find_min_value(arr, n):
    val = 0
    for i in range(n):
        val += math.log(arr[i])
    return binarysearch(n, min(arr), max(arr), val)

