def count_successful_photos(n, a):
    M = 10 ** 9 - 7
    m = sum(a)
    dp = [0] * (2 * m + 1)
    ans = 0
    dp[m] = 1
    for i in range(n):
        v = a[i]
        ndp = [0] * (2 * m + 1)
        for j in range(2 * m + 1):
            if j - v >= 0:
                ndp[j - v] += dp[j]
                if ndp[j - v] > M:
                    ndp[j - v] -= M
            if j + v <= 2 * m:
                ndp[j + v] += dp[j]
                if ndp[j + v] > M:
                    ndp[j + v] -= M
        dp = ndp
        ans += dp[m]
        ans %= M
        dp[m] += 1
    return ans % M

