from collections import defaultdict

def min_checkpoints_and_moves(points, c):
    d = defaultdict(list)
    for (x, y) in points:
        d[y - x, x % c].append(x)
    d = tuple(d.values())
    for l in d:
        l.sort()
    r = sum((l[-i - 1] - l[i] for l in d for i in range(len(l) // 2)))
    min_checkpoints = len(d)
    min_moves = r / c
    return (min_checkpoints, min_moves)

