    return 'YES'

def is_farm_ideal(s, n, k):
    if s < k:
        return 'NO'
    if s == k:
        return 'YES'
    if n == 1:
        return 'NO'
    r = s - n
    if k & 1 and r >= n and ((r - n) % 2 == 0):
        return 'NO'
    if n < k:
        return 'NO'
    if n >= k:
        if s - n >= n * k * k:
            return 'NO'
    return 'YES'
