    l_ = dict(enumerate('0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'))

    def to_base(n, base):
        li = []
        while n:
            (n, b) = divmod(n, base)
            li.append(l_[b])
        return li[::-1]
    c = i = 0
    r = ''
    while c < n:
        t = ''.join(to_base(i, base))
        if is_polydivisible(t, base):
            c += 1
            r = t
        i += 1
    return r or '0'

