def count_valid_email_substrings(s: str) -> int:
    """"""
    before = after = 0
    n = len(s)
    i = 0
    ans = 0
    while i < n:
        while i < n and s[i] != '@':
            if s[i].isalpha():
                before += 1
            if s[i] == '.':
                before = 0
            i += 1
        if i < n and s[i] == '@':
            i += 1
            ok = True
            temp = 0
            good = False
            while i < n and s[i] != '.':
                if s[i] == '_' or s[i] == '@':
                    ok = False
                    break
                if s[i].isalpha():
                    temp += 1
                i += 1
                good = True
            if not ok or not good:
                before = temp
                after = 0
                continue
            if i < n and s[i] == '.':
                i += 1
                while i < n and s[i].isalpha():
                    after += 1
                    i += 1
        ans += before * after
        before = after
        after = 0
    return ans
