def can_weigh_item(w, m):
    while m % w not in [0, 1, -1 + w]:
        if m % w == 0:
            m //= w
        elif m % w == 1:
            m = (m - 1) // w
        else:
            m = (m + 1) // w
        if m == 0:
            return 'YES'
    return 'NO'

