from collections import defaultdict

def reconstruct_permutation(n, q):
    C = defaultdict(list)
    for (i, triplet) in enumerate(q):
        for num in triplet:
            C[num].append(i)
    last = None
    seclast = None
    for num in C:
        if len(C[num]) == 1:
            last = num
            for k in q[C[num][0]]:
                if len(C[k]) == 2:
                    seclast = k
            break
    over = set([last, seclast])
    ans = [last, seclast]
    i = 1
    for _ in range(n - 2):
        key = ans[i]
        triplet_index = C[key][0]
        i += 1
        for k in q[triplet_index]:
            C[k].remove(triplet_index)
            if k not in over:
                over.add(k)
                ans.append(k)
    return ans

