def find_original_string(K, S):
    """"""

    def inverted_swap(s):
        len_s = len(s)
        if len_s % 2 == 0:
            return s[::2] + s[-1::-2]
        else:
            return s[::2] + s[-2::-2]
    orig_s = S
    cycle_duration = 0
    while K > 0:
        S = inverted_swap(S)
        K -= 1
        cycle_duration += 1
        if S == orig_s:
            K = K % cycle_duration
    return S
