import math

def calculate_min_extra_participants(n, weights):
    if n != 1:
        return 2
    d = int(math.log2(n - 1) + 1)
    weights.sort()
    cw = [1]
    for i in range(1, n):
        if weights[i] == weights[i - 1]:
            cw[-1] += 1
        else:
            cw.append(1)
    sl = [0] * (d + 1)
    c = 0
    s = 0
    for i in cw:
        s += i
        while s > 1 << c:
            c += 1
        sl[c] = s
    sr = [0] * (d + 1)
    c = 0
    s = 0
    for i in cw[-1::-1]:
        s += i
        while s > 1 << c:
            c += 1
        sr[c] = s
    ans = (1 << d) - n + 2
    for a in range(d):
        for b in range(d):
            lr = sl[a] + sr[b]
            c = 1
            while c < n - lr:
                c <<= 1
            ans = min(ans, (1 << a) + (1 << b) + c - n)
    return ans

