def insertion_sort_trace(arr, trace=False):
    """"""
    n = len(arr)
    if trace:
        print(*arr, sep=' ')
    for i in range(1, n):
        key = arr[i]
        j = i - 1
        while j >= 0 and arr[j] > key:
            arr[j + 1] = arr[j]
            j -= 1
        arr[j + 1] = key
        if trace:
            print(*arr, sep=' ')
    return arr
