def largest_permutation(N, K, arr):
    if K > N:
        return sorted(arr, reverse=True)
    i = 0
    while i < K and N > 0:
        if i != arr.index(N):
            (arr[arr.index(N)], arr[i]) = (arr[i], arr[arr.index(N)])
        else:
            K += 1
        i += 1
        N -= 1
    return arr

