def remove_reverse(S: str) -> str:
    S = list(S)
    char_count = {}
    for char in S:
        char_count[char] = char_count.get(char, 0) + 1
    (i, j) = (0, len(S) - 1)
    (left_part, right_part) = ('', '')
    is_left_to_right = False
    operation_count = 0
    while i <= j:
        if is_left_to_right:
            if char_count[S[i]] > 1:
                char_count[S[i]] -= 1
                i += 1
                is_left_to_right = False
                operation_count += 1
            else:
                left_part += S[i]
                i += 1
        elif char_count[S[j]] > 1:
            char_count[S[j]] -= 1
            j -= 1
            is_left_to_right = True
            operation_count += 1
        else:
            right_part = S[j] + right_part
            j -= 1
    final_string = left_part + right_part
    if operation_count % 2 == 1:
        final_string = final_string[::-1]
    return final_string

