def max_consecutive_standing_on_hands(N, K, S):
    ls = []
    for i in range(N):
        if i != N - 1 and S[i] != S[i + 1]:
            ls.append(i)
    length = len(ls)
    ls -= [N - 1] * (2 * K + 2)
    m = 0
    if S[0] == '0':
        for i in range(0, length, 2):
            x = ls[i + 2 * K + 1] - ls[i]
            if x > m:
                m = x
        x = ls[2 * K - 1] + 1
        if x > m:
            m = x
    else:
        for i in range(1, length, 2):
            x = ls[i + 2 * K + 1] - ls[i]
            if x > m:
                m = x
        x = ls[2 * K] + 1
        if x > m:
            m = x
    return m

