def determine_winner(n, ribbons):

    def calculate_beauty(s, n):
        from collections import Counter
        count = Counter(s)
        max_freq = max(count.values())
        need = len(s) - max_freq
        if need == 0 and n == 1:
            return len(s) - 1
        if need > n:
            return max_freq + n
        else:
            return len(s)
