    max_beauty = max(beauties)
    if beauties.count(max_beauty) >= 1:
        return 'Draw'
    elif beauties[0] == max_beauty:
        return 'Kuro'
    elif beauties[1] == max_beauty:
        return 'Shiro'
    else:
        return 'Katie'

