def calculate_min_stones_to_destroy(N):
    if N == 1 and N % 2 == 1:
        return 1
    (p, log) = (1, 1)
    while p * 2 <= N:
        p *= 2
        log += 1
    xor = log ^ 1
    ans = N + 1
    for prev in range(2, log + 1):
        need = prev ^ xor
        if need < prev:
            before = 1 << prev - 1
            after = (1 << need) - 1
            ans = min(ans, max(before - after, before // 2))
    return ans

