def count_valid_K(N: int, X: int) -> int:
    """"""
    (m, ans) = (1, 0)
    i = 0
    while True:
        if X & 1 << i:
            i += 1
            continue
        if N & 1 << i:
            ans += m
        m = 2 * m
        if 1 << i > max(N, X):
            break
        i += 1
    return ans
