def count_trailing_zeros_in_base(n, b):

    def get_prime_factors(goal):
        ans = []
        if goal % 2 == 0:
            cnt = 0
            while goal % 2 == 0:
                goal //= 2
                cnt += 1
            ans.append([2, cnt])
        i = 3
        while i * i <= goal:
            if goal % i == 0:
                cnt = 0
                while goal % i == 0:
                    goal //= i
                    cnt += 1
                ans.append([i, cnt])
            i += 2
        if goal != 1:
            ans.append([goal, 1])
        return ans

    def count_factors_in_factorial(g, p):
        ans = 0
        t = p
        while t <= g:
            ans += g // t
            t *= p
        return ans
    prime_factors = get_prime_factors(b)
    sz = len(prime_factors)
    min_trailing_zeros = count_factors_in_factorial(n, prime_factors[0][0]) / prime_factors[0][1]
    for i in range(1, sz):
        current_trailing_zeros = count_factors_in_factorial(n, prime_factors[i][0]) // prime_factors[i][1]
        if current_trailing_zeros < min_trailing_zeros:
            min_trailing_zeros = current_trailing_zeros
    return min_trailing_zeros

