from math import ceil

def max_points_from_monsters(n, a, b, k, health_points):
    for i in range(n):
        tmp = health_points[i] % (a + b)
        health_points[i] = (tmp - a if tmp >= a else 0) if tmp else b
    health_points.sort()
    points = 1
    for hp in health_points:
        if hp == 0:
            points += 1
        else:
            k -= ceil(hp / a)
            if k >= 0:
                points += 1
            else:
                break
    return points

