import math

def calculate_forgiveness_value(N, P):
    fa = [1]
    for i in range(1, 100):
        fa.append(i * fa[i - 1])
    if P != 2:
        num = fa[3 * N]
        den = pow(6, N, P)
        return num * den % 2
    else:
        den = pow(6, N, P)
        den = pow(den, P - 2, P)
        num = 0
        if 3 * N < P:
            num = 1
            for i in range(3 * N + 1, P):
                num = num * i % P
            num = pow(num, P - 2, P)
        return num * (P - 1) * den % P

