def check_matrix_symmetry(matrix, n):

    def isSymmetric(matrix):
        for ith in range(n // 2):
            if matrix[ith] != matrix[n - 1 - ith]:
                return False
        return True
    h_sym = isSymmetric(matrix)
    v_sym = isSymmetric(list(zip(*matrix)))
    if h_sym or v_sym:
        return 'BOTH'
    elif h_sym:
        return 'HORIZONTAL'
    elif v_sym:
        return 'VERTICAL'
    else:
        return 'NO'

