def sum_complex_numbers(arr):
    if not arr:
        return ''
    sub = {'1i': 'i', '-1i': '-i', '0i': '0'}
    total_sum = sum((complex(x.replace('i', 'j')) for x in arr))
    result = str(total_sum).replace('j', 'i')
    result = result.strip('()')
    result = result.replace('+0i', '')
    return sub.get(result, result)

