def max_soldiers_to_boss(m, n, k, t, agility, traps):
    agility.append(10 ** 15)
    agility.sort(reverse=True)
    l = 1
    r = m + 1
    while r - l > 1:
        mid = (r + l) // 2
        D_limit = agility[mid]
        P = [0] * (n + 2)
        for (L, R, D) in traps:
            if D_limit < D:
                P[L] += 1
                P[R + 1] -= 1
        c = 0
        for i in range(n + 1):
            P[i + 1] += P[i]
            if P[i + 1] > 0:
                c += 1
        time = n + 1 + 2 * c
        if time <= t:
            l = mid
        else:
            r = mid
    return l

