def make_arrays_identical(A, B, K):
    mod = 1000000008
    f = [1]
    for i in range(1, 200002):
        f.append(i * f[i - 1] % mod)
    n = len(A)
    p = list(range(n + 1))
    s = [1] * (n + 1)

    def prnt(x):
        if p[x] != x:
            p[x] = prnt(p[x])
        return p[x]
    for i in range(1, n + 1):
        aa = prnt(A[i - 1])
        bb = prnt(B[i - 1])
        if aa != bb:
            p[bb] = aa
            s[aa] += s[bb]
    ans1 = 0
    ans2 = 1
    for i in range(1, n + 1):
        if prnt(i) == i and s[i] > 1:
            ans1 += s[i] - 1
            ans2 = ans2 * f[s[i]] % mod
    ans2 = ans2 * f[ans1] % mod
    if K == 1:
        return ans1
    elif K == 2:
        return (ans1, ans2)

