def max_aesthetic_colors(n):

    def prime_factor(n):
        factors = []
        for i in range(2, int(n ** 0.5) + 1):
            while n % i == 0:
                factors.append(i)
                n = n // i
        if n != 1:
            factors.append(n)
        return factors
    p = list(set(prime_factor(n)))
    if len(p) != 1:
        return p[0]
    else:
        return 1

