def chain_arith_deriv(start, k):

    def prime_factors(n):
        if n < 2:
            return []
        factors = []
        for k in (2, 3):
            while n % k == 0:
                n //= k
                factors.append(k)
        k = 5
        step = 2
        while k * k <= n:
            if n % k:
                k += step
                step = 6 - step
            else:
                n //= k
                factors.append(k)
        if n > 1:
            factors.append(n)
        return factors

    def arith_deriv(n):
        factors = prime_factors(n)
        return sum((n * factors.count(factor) // factor for factor in set(factors))) or 1
    if len(prime_factors(start)) <= 2:
        return f'{start} is a prime number'
    chain = [start]
    while k > 1:
        start = arith_deriv(start)
        chain.append(start)
        k -= 1
    return chain

