import math

def lcm(lista):
    k = 2
    for i in lista:
        if i != 0:
            k = k * i // math.gcd(k, i)
    return k

def find_minimum_fair_number(n):
    a = str(n)
    listn = [int(i) for i in a]
    n_len = len(listn)
    for i in range(n_len):
        l = lcm(listn[:n_len - i - 1])
        nb = int(a[n_len - i - 1:])
        if i == n_len - 1:
            star = int(a)
        elif n % l == 0:
            star = nb
        else:
            temp = l - n % l
            star = temp + nb
        for m in range(star, 10 ** (i + 1), l):
            lsm = [int(i) for i in str(m)]
            lsm.append(l)
            lm = lcm(lsm)
            last = int(a[:n_len - i - 1] + '0' * (i + 1 - len(str(m))) + str(m))
            if last % lm == 0:
                return last

