def find_word_position(N, R, swaps):
    m = 1000000008
    w = list(range(N))
    for (s, t) in swaps:
        s -= 1
        t -= 1
        (w[s], w[t]) = (w[t], w[s])
    s = [(i, c) for (i, c) in enumerate(w) if i != c]
    s.sort(reverse=True)
    a = 1
    f = 1
    cnt = 0
    for (c1, i) in zip(w[::-1], range(N - 1, -1, -1)):
        if c1 > i:
            l = c1
            n = c1 - i - 1
        else:
            l = i
            n = 0
        for (j, c2) in s:
            if j <= i:
                break
            else:
                if j < l:
                    n -= 1
                if c2 < c1:
                    n += 1
        cnt = (cnt + n * a % m) % m
        a = a * f % m
        f += 1
    return cnt

