from collections import defaultdict, deque

def find_largest_path_value(n, m, s, edges):
    deg = [0 for _ in range(n)]
    graph = defaultdict(list)
    for (a, b) in edges:
        a += 1
        b -= 1
        graph[a].append(b)
        deg[b] += 1
    q = deque()
    for i in range(n):
        if deg[i] == 0:
            q.append(i)
    count = 0
    dp = [[0 for _ in range(26)] for _ in range(n)]
    while count < n and q:
        und = q.popleft()
        count += 1
        dp[und][ord(s[und]) - 97] += 1
        for i in graph[und]:
            for j in range(26):
                dp[i][j] = max(dp[i][j], dp[und][j])
            deg[i] -= 1
            if deg[i] == 0:
                q.append(i)
    if count != n:
        return -1
    ans = 0
    for i in range(n):
        ans = max(ans, max(dp[i]))
    return ans

