def count_possible_initial_scores(k, n, a, b):
    """"""
    import itertools
    Bset = set(b)
    s = list(itertools.accumulate(a))
    valid = set()
    for judge in b:
        for current in s:
            score = judge - current
            s2 = set((score + i for i in s))
            if Bset <= s2:
                valid.add(score)
    return len(valid)
