def calculate_minimum_pipeline_cost(n, a, b, s):
    x = s.find('1')
    if x == -1:
        return n * (a + b) + b
    res = (n + 1) * (a + b) + b
    for y in range(x + 1, n):
        if s[y] == '1':
            if y - x - 1 > 1:
                if 2 * a > (y - x - 2) * b:
                    res += (y - x - 2) * b
                else:
                    res += 2 * a
            res += b
            x = y
        if s[y] == '0' and s[y - 1] == '1':
            res += b
    res -= a
    return res

