def count_palindromic_numbers(A: int, B: int) -> int:
    """"""

    def is_palindromic(num: int) -> bool:
        """Helper function to check if a number is palindromic."""
        s = str(num)
        return s == s[::-1]
    count = 0
    for i in range(A, B + 1):
        if is_palindromic(i):
            count += 1
    return count
