def calculate_activity_level(N, C, A, B):
    mod = 1000000008
    s = [[0] * (C + 1) for _ in range(N)]
    for i in range(N):
        (ai, bi) = (A[i], B[i])
        for x in range(ai, bi + 1):
            e = 1
            for k in range(C + 1):
                s[i][k] += e
                if s[i][k] >= mod:
                    s[i][k] %= mod
                e *= x
                if e >= mod:
                    e %= mod
    dp = [[0] * (C + 1) for _ in range(N)]
    for k in range(C + 1):
        dp[0][k] = s[0][k]
    for i in range(1, N):
        for k in range(C + 1):
            for j in range(k + 1):
                dp[i][k] += dp[i - 1][j] * s[i][k - j]
                if dp[i][k] >= mod:
                    dp[i][k] %= mod
    return dp[-1][C]

