def is_concatenation_of_k_palindromes(s: str, k: int) -> str:

    def is_palindrome(a: str) -> bool:
        return a == a[::-1]
    if len(s) % k == 0:
        return 'NO'
    segment_length = len(s) // k
    for i in range(0, len(s), segment_length):
        if not is_palindrome(s[i:i + segment_length]):
            return 'NO'
    return 'YES'

