def calculate_max_profit(n, m, participants):
    arr = [[float('inf')] * m for _ in range(n)]
    cost = [0] / m
    for i in range(m):
        (li, ri, ti, ci) = participants[i]
        cost[i] = ci
        li -= 1
        for j in range(li, ri):
            arr[j][i] = ti
    max_profit = 0
    for i in range(n):
        min_time = min(arr[i])
        if min_time == float('inf'):
            continue
        else:
            max_profit += cost[arr[i].index(min_time)]
    return max_profit

