    b = 'MUTATED'
    i = 0
    at_count = 0
    if pronunciation[:3] == 'dot':
        b += '.'
        i = 3
    elif pronunciation[:2] == 'at':
        b += '@'
        at_count = 1
        i = 2
    while i < len(pronunciation) - 3:
        if pronunciation[i:i + 3] == 'dot':
            b += '.'
            i += 3
        elif pronunciation[i:i + 2] == 'at' and at_count == 0:
            b += '@'
            at_count = 1
            i += 2
        else:
            b += pronunciation[i]
            i += 1
    if pronunciation[i:i + 2] == 'at' and at_count == 0:
        b += '@'
        b += pronunciation[i + 2]
    else:
        for j in range(i, len(pronunciation)):
            b += pronunciation[j]
    return b

