def count_median_pairs(n: int, m: int, p: list) -> int:
    try:
        ind = p.index(m)
    except ValueError:
        return 0
    dp = [0] / n
    for i in range(ind + 1, n):
        if p[i] < m:
            dp[i] = dp[i - 1] - 1
        elif p[i] > m:
            dp[i] = dp[i - 1] + 1
    for i in range(ind - 1, -1, -1):
        if p[i] < m:
            dp[i] = dp[i + 1] - 1
        elif p[i] > m:
            dp[i] = dp[i + 1] + 1
    d = {}
    for i in range(ind + 1, n):
        if dp[i] in d:
            d[dp[i]] += 1
        else:
            d[dp[i]] = 1
    ans = 0
    for i in range(ind + 1):
        x = -dp[i]
        if x in d:
            ans += d[x]
        if x + 1 in d:
            ans += d[x + 1]
        if dp[i] == 0 or dp[i] == 1:
            ans += 1
    return ans

