    (lo, hi) = (0, 10 ** 9)
    potential_alarm_hour = 1
    while lo <= hi:
        mid = lo + (hi - lo) // 2
        speeds = (h + mid * a for (h, a) in zip(H, A))
        fast_speeds = filter(lambda x: x >= L, speeds)
        if sum(fast_speeds) >= M:
            potential_alarm_hour = mid
            hi = mid - 1
        else:
            lo = mid + 1
    return potential_alarm_hour

