def largest_min_distance(n, k, stalls):
    if len(stalls) <= k:
        return -1

    def can_place_cows(mid):
        count = 1
        latest_put_cow = 0
        for i in range(1, len(stalls)):
            if stalls[i] - stalls[latest_put_cow] >= mid:
                count += 1
                latest_put_cow = i
        return count
    stalls.sort()
    (left, right) = (1, stalls[-1] - stalls[0])
    ans = left
    while left <= right:
        mid = left + (right - left) // 2
        if can_place_cows(mid) >= k:
            ans = mid
            left = mid + 1
        else:
            right = mid - 1
    return ans

