def maximize_bid_volume(n, traffic_volumes, connections):
    dp = [[0, 0] for _ in range(n)]
    adjacency_list = {i: [] for i in range(n)}
    for (a, b) in connections:
        adjacency_list[a - 1].append(b - 1)
        adjacency_list[b - 1].append(a - 1)

    def dfs(station, parent, visited):
        dp[station][1] = traffic_volumes[station]
        for neighbor in adjacency_list[station]:
            if neighbor not in visited:
                visited.add(neighbor)
                dfs(neighbor, station, visited)
                dp[station][0] += max(dp[neighbor][0], dp[neighbor][1])
                dp[station][1] += dp[neighbor][0]
    visited = set()
    visited.add(1)
    dfs(0, -1, visited)
    return max(dp[0][0], dp[0][1])

