def max_blocks_to_remove(n, m, a):
    """"""
    if n == 1:
        return 0
    heights = sorted(a, reverse=True)
    used = 0
    needed = heights[0]
    for current in heights:
        if needed <= current:
            used += 1
            needed -= 1
        else:
            used += needed - current
            used += 1
            needed = current - 1
    if needed > 0:
        used += needed
    return sum(a) - used
