    for houses in test_cases:
        t = len(houses)
        li = sorted(houses)
        dp = [li[1] - li[0]] + [0] * (t - 2) + [li[t - 1] - li[t - 3]]
        for i in range(1, t - 1):
            dp[i] = min(li[i] - li[i - 1], li[i + 1] - li[i])
        results.append(max(dp))
    return results

